import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { RootState } from "../../app/store";

const apiURL = process.env.SEND_EMAIL;

// メール送信処理
// export const fetchAsyncSendEmail = (values) => {
//   console.log(apiURL);
//   console.log(values);
// };
export const fetchAsyncSendEmail = createAsyncThunk(
  "send/email",
  async (values) => {
    const res = await axios.get(`http://localhost:4000/send_email`, {
      headers: {
        "Content-Type": "application/json",
      },
    });
    console.log(res);
    return res.data;
  }
);
const emailSlice = createSlice({
  name: "email",
  initialState: {},
  reducers: {},
  // extraReducer追加
});

console.log(emailSlice);

// export const { fetchAsyncSendEmail } = emailSlice.actions;

// Reducerをエクスポートする
export default emailSlice;
