import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { RootState } from "../../app/store";

const apiURL = process.env.SEND_EMAIL;

// メール送信処理
export const fetchAsyncSendEmail = createAsyncThunk(
  "send/email",
  async (values) => {
    console.log(res);
    const res = await axios.get(`http://localhost:4000/send_email`, {
      headers: {
        "Content-Type": "application/json",
      },
    });

    return res.data;
  }
);
const emailSlice = createSlice({
  name: "email",
  initialState: {
    send: false,
  },
  // reducers: {},
  // extraReducer追加
  extraReducers: (builder) => {
    // fetchAsyncLoginメソッドがfulfilledだった場合の処理
    // ログインが成功した場合はJWTをローカルストレージに格納
    builder.addCase(fetchAsyncLogin.fulfilled, (state, action) => {
      localStorage.setItem("localJWT", action.payload.access);
    });

    builder.addCase(fetchAsyncCreateProf.fulfilled, (state, action) => {
      state.myprofile = action.payload;
    });

    builder.addCase(fetchAsyncGetProfs.fulfilled, (state, action) => {
      state.profiles = action.payload;
    });
    builder.addCase(fetchAsyncGetMyProf.fulfilled, (state, action) => {
      state.myprofile = action.payload;
    });
  },
  // extraReducer追加
});

console.log(emailSlice);

// export const { fetchAsyncSendEmail } = emailSlice.actions;

// Reducerをエクスポートする
export default emailSlice;
