import React from "react";
import "./top.css";
import "bootstrap/dist/css/bootstrap.min.css";
// reactのheadやmetaタグを設定できるライブラリ（今回はbodyタグの設定で使う)
import { Helmet } from "react-helmet";
import {
  Container,
} from "react-bootstrap";
import { Formik } from "formik";
import * as Yup from "yup";
import Header from '../common/Header';
import Fadein from '../animation/Fadein';
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

import {fetchAsyncSendEmail} from '../email/emailSlice';


// スムーススクロールを実現するためのライブラリ
import { SmuuthLink, AnimateScroll as scroll } from "react-scroll";

const Top = () => {
  const dispatch = useDispatch();
  return (
    <div>
      <Helmet>
        <title>My Portfolio</title>
        <style>{"body { background-color: #000000; }"}</style>
      </Helmet>
      <div>
        <header>
          <Header />
        </header>
        <div className="content-part">
          <Container fluid>
            <div className="row">
              <div className=" first-content-img col-12 col-lg-12 col-md-12 col-sm-12">
                <div id="home">
                  <h1 className="first-block-sentence content-character mt-5">
                    I'm Tatsuki.
                  </h1>
                  <h1 className="first-block-sentence content-character mt-5">
                    This is my portfolio as a
                  </h1>
                  <h1 className="first-block-sentence content-character mt-5">
                    software engineer.
                  </h1>
                </div>
              </div>
            </div>
          </Container>
          <Container fluid className="second-block">
            <div className="row">
              <div className="second-content-img col-12 col-lg-12 col-md-12 col-sm-12">
                <div className="firstvlock introduction">
                <h1 className="content col-12" id="about">About me</h1>
                <div className="col-12 mx-auto">
                  <p>
                    2014 Mar: Graduated from Kobe Gakuin University.
                  </p>
                  <p>
                    2014 April: Work as a Operator for MS System Inc.
                  </p>
                  <p>
                    2016 Feb: Work as an assistanece of system engineer for Soft Hyperion Inc.
                  </p>
                  <p>
                    2018 Mar: Work as a programmer for Casareal inc.
                  </p>
                  </div>
                </div>
              </div>
            </div>
          </Container>
          <Container fluid className="third-block">
            <div className="row">
              <div className="third-content-img col-12 col-lg-12">
                <div className="col-12">
                  <h1 className="content  mx-auto" id="contact">Contact</h1>
                  <Formik
                    initialErrors={{ email: "required", message: "required" }}
                    initialValues={{ email: "", subject: "", message: "" }}
                    onSubmit={async (values) => {
                      const res = await axios.get('http://localhost:4000/send_email')
                      await dispatch(fetchAsyncSendEmail(values));
                    }}
                    validationSchema={Yup.object().shape({
                      email: Yup.string()
                        .email("email format is wrong")
                        .required("email is must"),
                      message: Yup.string()
                        .required("message is must")
                        .max(2000)
                    })}
                    render={({
                      handleSubmit,
                      handleChange,
                      handleBlur, // handler for onBlur event of form elements
                      values,
                      touched,
                      errors,
                      isValid,
                    }) => (
                      <>
                        <form onSubmit={handleSubmit}>
                          <div className="mb-3">
                            <input
                              className="form-control col-12 mt-1"
                              placeholder="Email Address"
                              type="text"
                              name="email"
                              onBlur={handleBlur}
                              onChange={handleChange}
                            />
                            {touched.email && errors.email ? (
                              <div className="error-message">{errors.email}</div>
                            ) : null}
                          </div>           
                          <div className="mb-3">
                            <input
                              className="form-control col-12 mt-1"
                              placeholder="Subject"
                              type="text"
                              name="subject"
                              onBlur={handleBlur}
                              onChange={handleChange}
                            />
                          </div>
                          <div>
                            <textarea
                              className="form-control mt-1"
                              rows="10"
                              placeholder="Message"
                              name="message"
                              onBlur={handleBlur}
                              onChange={handleChange}
                            />
                            {touched.message && errors.message ? (
                              <div className="error-message">{errors.message}</div>
                            ) : null}
                          </div>
                          <br />
                          <input
                            className="col-12 contact-send-button"
                            type="submit"
                            value="Send"
                          ></input>
                        </form>
                      </>
                    )}
                  />
                </div>
              </div>
            </div>
          </Container>
        </div>
      </div>
    </div>
  );
};

export default Top;
