import { configureStore, ThunkAction, Action } from "@reduxjs/toolkit";
import { combineReducers } from "redux";
import emailReducer from "../features/email/emailSlice";

export const store = configureStore({
  reducer: {
    email: emailReducer,
  },
});

export default store;
