import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { RootState } from "../../app/store";

const apiURL = process.env.SEND_EMAIL;

// メール送信処理
export const fetchAsyncSendEmail = createAsyncThunk(
  "send/email",
  async (values) => {
    console.log(res);
    const res = await axios.get(`http://localhost:4000/send_email`, {
      headers: {
        "Content-Type": "application/json",
      },
    });

    return res.data;
  }
);
const emailSlice = createSlice({
  name: "email",
  initialState: {
    send: false,
  },
  reducers: {
    increment: (state) => {
      // Redux Toolkit allows us to write "mutating" logic in reducers. It
      // doesn't actually mutate the state because it uses the Immer library,
      // which detects changes to a "draft state" and produces a brand new
      // immutable state based off those changes
      send = true;
    },
  },
  // extraReducer追加
  // extraReducers: (builder) => {
  //   // fetchAsyncLoginメソッドがfulfilledだった場合の処理
  //   // ログインが成功した場合はJWTをローカルストレージに格納
  //   builder.addCase(fetchAsyncSendEmail.fulfilled, (state, action) => {
  //     localStorage.setItem("localJWT", action.payload.access);
  //   });
  // },
  // extraReducer追加
});

export const { increment } = emailSlice.actions;

console.log(emailSlice);

// export const { fetchAsyncSendEmail } = emailSlice.actions;

// Reducerをエクスポートする
export default emailSlice;
