import { configureStore, combineReducers } from "@reduxjs/toolkit";

import emailSlice from "../features/email/emailSlice";

const rootReducer = combineReducers({
  email: emailSlice,
});

export const setupStore = () => {
  const store = configureStore({
    reducer: rootReducer,
  });
  return store;
};
